UPDATE BVR.LINK_PGTO SET STATUS = '$status$', DATA_PRIMEIRO_BLOQUEIO = TO_TIMESTAMP($dataPrimeiroBloq$,'DD/MM/YYYY HH24:MI:SS.XFF'), DATA_PRIMEIRO_DESBLOQUEIO = TO_TIMESTAMP($dataPrimeiroDesbloq$,'DD/MM/YYYY HH24:MI:SS.XFF'), DATA_SEGUNDO_BLOQUEIO = TO_TIMESTAMP($dataSegundoBloq$,'DD/MM/YYYY HH24:MI:SS.XFF') WHERE COD_LINK_PGTO = $codLink$